        private ArrayList<ArrayList<Integer>> narrow = new ArrayList<ArrayList<Integer>>();
    //copy all to narrow every time a new game is played where the computer guesses
        public makeGuess(){
        //copies the int[][] all into a 2d ArrayList
        for (int i=0; i<all.length; i++){
            ArrayList<Integer> one = new ArrayList<Integer>();
            for (int j=0; j<4; j++){
                one.add(all[i][j]);
            }
            narrow.add(one);
        }
    }


    /*true if the specified part of narrow does not have the same number of matches with 
      the previous guess as pegs the previous guess received*/
    public boolean nomatch(ArrayList<Integer> checking, int[] lastG, int times){
        int matches =0;
        ArrayList<Integer> unmatched = new ArrayList<Integer>(4);
        for (int i: lastG)
            unmatched.add(i);
        for (int i=0; i<4; i++){
            for (int j=0; j<unmatched.size(); j++){
                if (checking.get(i) == unmatched.get(j)){
                matches+=1;
                unmatched.remove(j);
                break;
                }
            }
        }
        return (matches!=times);
    }



    /*true if the specified part of narrow does not have the same number of exact matches with 
      the previous guess as red pegs the previous guess received*/
     public boolean noperfmatch(ArrayList<Integer> checking, int[] lastG, int perf){
        int matches =0;
         for (int i=0; i<4; i++){
            if (checking.get(i) == lastG[i]){
                matches+=1;
            }
        }
         return (matches!=perf);
    }

    //counts the pegs, runs the information through the checking methods
    public int match(int index, ArrayList<Integer> checking, int[] lastG, int red, int white){
        int total = red +white;
        if (noperfmatch(checking, lastG, red)){
            narrow.remove(index);
            return 0;
        }
        else if (nomatch(checking, lastG, total)){
            narrow.remove(index);
            return 0;
         }
        return 1;
    }

    //counts pegs of each type, calls the match method with the new information
    public int check(int index, ArrayList<Integer> checking, int[] lastG, int[] pegs){
        //count the pegs of each type
        int red=0;
        int white=0;
        for (int i=0; i<4; i++){
            if (pegs[i]==2){
                red+=1;
            }
            else if (pegs[i]==1){
                white+=1;
            }
        }
        return match(index, checking, lastG, red, white);
    }


    //go through every part of narrow and check it using pegs and the last guess
    public ArrayList<Integer> guess(int[] lastG, int[] pegs){
        int i=0;
        while (i<narrow.size()){
            //index it if the checked item is not removed, otherwise do not index
            i+=check(i, narrow.get(i), lastG, pegs); //checks each part of narrow
        }
        //return the first element of the updated narrow
	return narrow.get((int)(Math.random()*narrow.size()));
    }
